import "./BeansOfTheDay.css";

export default function BeansOfTheDay({ beans }) {
  const botdBeans = beans.filter(bean => bean.isBOTD);

  if (botdBeans.length === 0) return null;

  return (
    <section className="botd">
      <h2>â˜• Beans of the Day</h2>

      <div className="botd-list">
        {botdBeans.map(bean => (
          <div className="botd-card" key={bean._id}>
            <img src={bean.Image} alt={bean.Name} />

            <div className="botd-info">
              <span className="badge">Bean of the Day</span>
              <h3>{bean.Name}</h3>
              <p>{bean.Description.slice(0, 120)}...</p>
              <p className="price">{bean.Cost}</p>
            </div>
          </div>
        ))}
      </div>
    </section>
  );
}
