import { useState } from "react";
import beans from "../data/AllTheBeans.json";
import "./CoffeeGrid.css";

export default function CoffeeGrid() {
  const [search, setSearch] = useState("");

  const filteredBeans = beans.filter(bean =>
    bean.Name.toLowerCase().includes(search.toLowerCase()) ||
    bean.Country.toLowerCase().includes(search.toLowerCase()) ||
    bean.colour.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div className="coffee-page">
      {/* Filter bar */}
      <input
        type="text"
        placeholder="Search beans, country, or roast..."
        value={search}
        onChange={(e) => setSearch(e.target.value)}
        className="search-bar"
      />

      {/* Grid */}
      <div className="coffee-grid">
        {filteredBeans.map(bean => (
          <div className="coffee-card" key={bean._id}>
            <img src={bean.Image} alt={bean.Name} />
            <h3>{bean.Name}</h3>
            <p className="roast">{bean.colour}</p>
            <p className="country">{bean.Country}</p>
            <p className="price">{bean.Cost}</p>
          </div>
        ))}
      </div>
    </div>
  );
}
