import { useState } from "react";
import beans from "./beans.json";
import BeanCard from "./BeanCard";
import BeansOfTheDay from "./BeansOfTheDay";
import "./CoffeeGrid.css";

export default function CoffeeGrid() {
  const [search, setSearch] = useState("");

  const filteredBeans = beans.filter(bean =>
    bean.Name.toLowerCase().includes(search.toLowerCase()) ||
    bean.Country.toLowerCase().includes(search.toLowerCase()) ||
    bean.colour.toLowerCase().includes(search.toLowerCase())
  );

  const handleAddToOrder = (bean) => {
    console.log("Added to order:", bean.Name);
  };

  const handleViewDetails = (bean) => {
    console.log("View details:", bean.Name);
  };

  return (
    <div className="coffee-page">
      {/* Beans of the Day */}
      <BeansOfTheDay beans={beans} />

      {/* Search */}
      <input
        className="search-bar"
        type="text"
        placeholder="Search beans, country, or roast..."
        value={search}
        onChange={(e) => setSearch(e.target.value)}
      />

      {/* Main grid */}
      <div className="coffee-grid">
        {filteredBeans.map(bean => (
          <BeanCard
            key={bean._id}
            bean={bean}
            onAddToOrder={handleAddToOrder}
            onViewDetails={handleViewDetails}
          />
        ))}
      </div>
    </div>
  );
}
