import { useState } from "react";
import BeanModal from "./BeanModal";
import useAddToOrder from "../hooks/useAddToOrder";

export default function BeanCard({ bean, onAddToOrder, onViewDetails }) {
    const [open, setOpen] = useState(false);
    const { addToOrder } = useAddToOrder();
  return (
    <div className="coffee-card">
      {bean.isBOTD && <span className="botd-badge">Todayâ€™s Pick</span>}

      <img src={bean.Image} alt={bean.Name} />

      <h3>{bean.Name}</h3>
      <p className="roast">{bean.colour}</p>
      <p className="country">{bean.Country}</p>
      <p className="price">{bean.Cost}</p>

      <div className="card-actions">
        <button
          className="details-btn"
          onClick={() => setOpen(true)}
        >
          More Details
        </button>

        <button
          className="add-btn"
          onClick={() => addToOrder(bean)}
        >
          Add to Order
        </button>
      </div>
      {open && <BeanModal bean={bean} onClose={() => setOpen(false)} />}
    </div>
  );
}
