import beans from "../data/AllTheBeans.json"
import TodayPickBadge from "./parts/TodayPickBadge";
import "./BeansOfTheDay.css";

export default function BeansOfTheDay() {
  const beansOfTheDay = beans.filter(bean => bean.isBOTD);

  if (beansOfTheDay.length === 0) return null;

  return (
    <section className="botd-section">
      <h2>Beans of the Day</h2>

      <div className="botd-grid">
        {beansOfTheDay.map(bean => (
          <div className="botd-card" key={bean._id}>
            <TodayPickBadge />

            <img src={bean.Image} alt={bean.Name} />

            <div className="botd-content">
              <h3>{bean.Name}</h3>
              <p className="botd-roast">{bean.colour}</p>
              <p className="botd-country">{bean.Country}</p>
              <p className="botd-price">{bean.Cost}</p>
            </div>
          </div>
        ))}
      </div>
    </section>
  );
}
