export default function BeanModal({ bean, onClose }) {
  return (
    <div className="modal-backdrop" onClick={onClose}>
      <div className="modal" onClick={(e) => e.stopPropagation()}>
        <button className="close" onClick={onClose}>Ã—</button>

        <img src={bean.Image} alt={bean.Name} />

        <h2>{bean.Name}</h2>
        <p><strong>Roast:</strong> {bean.colour}</p>
        <p><strong>Country:</strong> {bean.Country}</p>
        <p><strong>Price:</strong> {bean.Cost}</p>

        <p className="description">{bean.Description}</p>

        <button className="primary full">Add to order</button>
      </div>
    </div>
  );
}
